        protected override void doLoad(CDevBase vDevice)
        {
            CmcDevAcFilter vDev;
            vDev = (CmcDevAcFilter)vDevice;

            base.doLoad(vDevice);

            vDev.UacN = RwAdo.ReadDouble("UacN");
            vDev.QacN  =RwAdo.ReadDouble("QacN") ;
            vDev.QacfN = RwAdo.ReadDouble("QacfN") ;
          
        }

        /// 
