        {
            CmcDevAcFilter vDev;
            vDev = (CmcDevAcFilter)vDevice;

            base.doSave(vDevice);

            RwAdo.SaveField("UacN", vDev.UacN);
            RwAdo.SaveField("QacN", vDev.QacN);
            RwAdo.SaveField("QacfN", vDev.QacfN);


        }

